export default function ElementReplacement () {
  var converter = findConverterForNode(node, converters, options)
  var content = process(node, converters, options)
  var whitespace = node.flankingWhitespace
  if (whitespace.leading || whitespace.trailing) content = content.trim()

  return (
    whitespace.leading +
    converter.replacement.call(converter, content, node, options) +
    whitespace.trailing
  )
}

