mapper.select:
SELECT
    PART_NUMBER,
    MAIN_PROGRAM_NAME,
    PROCESS_CODE,
    TESTER_MODEL,
    ITEM_NAME,
    MAIN_BIN,
    SUB_BIN,
    NG_BIN
FROM BIN_DESCRIPTION

    1 = 1

        AND PART_NUMBER=#{partNumber}


        AND MAIN_PROGRAM_NAME=#{mainProgramName}


        AND PROCESS_CODE=#{processCode}


        AND TESTER_MODEL=#{testerModel}


        AND ITEM_NAME=#{itemName}


        AND MAIN_BIN=#{mainBin}


        AND SUB_BIN=#{subBin}


        AND NG_BIN=#{ngBin}
