mapper.select:
SELECT
      SUBSTR(CREATE_DATE,1,4)||'-'||SUBSTR(CREATE_DATE,5,2)||'-'||SUBSTR(CREATE_DATE,7,2)||' '||SUBSTR(CREATE_DATE,9,2)||':'||SUBSTR(CREATE_DATE,11,2)||':'||SUBSTR(CREATE_DATE,13,2) AS CREATE_DATE,
      USER_ID,
      UI_ID
FROM
      COM_USER_USAGE

      1 = 1

          AND CREATE_DATE >= #{createDateStart}


          AND CREATE_DATE <= #{createDateEnd}


      AND USER_ID = #{userId}


      AND UI_ID = #{uiId}


ORDER BY
      CREATE_DATE DESC
