SELECT TESTER_MODEL, PROCESS_CODE, PART_NUMBER, MAIN_PROGRAM_NAME, GRADE, FAB, FIRMWARE_NAME, FIRMWARE_VERSION, FUNCTION_KEY, TEMPERATURE, TEMPERATURE_GUARD, SBL_YIELD_LIMIT, SBL_BIN9_COUNTER, PRODUCT_NAME, CUSTOMER, PACKAGE_TYPE, PROGRAM_VERSION, TESTER_OS_VERSION, PARA, DUT_MAP, BIN_DESCRIPTION, PROGRAM_DIRECTORY, CREATE_USER, CREATE_DATE, MODIFY_USER, MODIFY_DATE FROM PROGRAM_REGISTER
1 = 1
AND TESTER_MODEL=#{testerModel}
AND PART_NUMBER=#{partNumber}
AND PROCESS_CODE=#{processCode}
AND CREATE_DATE=#{createDate}

