mapper.select:
SELECT
    A.PART_NUMBER,
    A.OPERATOR_ID,
    A.LOT_ID,
    SUBSTR(A.SHOT_START_TIME,1,4)||'-'||SUBSTR(A.SHOT_START_TIME,5,2)||'-'||SUBSTR(A.SHOT_START_TIME,7,2)||' '||SUBSTR(SHOT_START_TIME,9,2)||':'||SUBSTR(A.SHOT_START_TIME,11,2)||':'||SUBSTR(A.SHOT_START_TIME,13,2) AS SHOT_START_TIME,
    SUBSTR(A.SHOT_END_TIME,1,4)||'-'||SUBSTR(A.SHOT_END_TIME,5,2)||'-'||SUBSTR(A.SHOT_END_TIME,7,2)||' '||SUBSTR(SHOT_END_TIME,9,2)||':'||SUBSTR(A.SHOT_END_TIME,11,2)||':'||SUBSTR(A.SHOT_END_TIME,13,2) AS SHOT_END_TIME,
    A.PROCESS_CODE,
    A.TESTER_NUMBER,
    A.TESTER_MODEL,
    A.HEAD,
    A.TEST_COUNTER,
    A.HANDLER_MODEL,
    ROUND((TO_DATE(A.Shot_End_Time,'YYYYMMDDHH24MISS') - TO_DATE(A.shot_start_time,'YYYYMMDDHH24MISS'))*86400) AS TEST_TIME ,
    ROUND((TO_DATE((SELECT MAX(B.Shot_End_Time) FROM CASI_BIN B WHERE B.Shot_End_Time < A.Shot_End_Time),'YYYYMMDDHH24MISS') - TO_DATE(A.shot_start_time,'YYYYMMDDHH24MISS'))*86400) AS INDEX_TIME
    A.BOARD_ID,
    A.UT_MAIN_BIN
FROM CASI_BIN A

    1 = 1

        AND A.END_TIME >= #{endTimeStart}


        AND A.END_TIME <= #{endTimeEnd}


        AND A.PART_NUMBER=#{partNumber}


        AND A.PROCESS_CODE=#{processCode}


        AND A.TESTER_MODEL=#{testerModel}


        AND A.TESTER_NUMBER=#{testerNumber}


        AND A.HEAD=#{head}


        AND A.TEST_COUNTER=#{testCounter}


        AND A.LOT_ID=#{lotId}


        AND A.MAIN_PROGRAM_NAME=#{mainProgramName}


        AND A.BOARD_ID=#{boardId}

SELECT
PART_NUMBER,
OPERATOR_ID,
LOT_ID,
LOT_IN_TIME,
END_TIME,
PROCESS_CODE,
TESTER_NUMBER,
TESTER_MODEL,
HEAD,
TEST_COUNTER,
HANDLER_MODEL,
BOARD_ID,
DUT_MAIN_BIN
FROM CASI_BIN

    1 = 1

        AND END_TIME >= #{endTimeStart}


        AND END_TIME <= #{endTimeEnd}


        AND PART_NUMBER=#{partNumber}


        AND PROCESS_CODE=#{processCode}


        AND TESTER_MODEL=#{testerModel}


        AND TESTER_NUMBER=#{testerNumber}


        AND HEAD=#{head}


        AND TEST_COUNTER=#{testCounter}


        AND LOT_ID=#{lotId}


        AND MAIN_PROGRAM_NAME=#{mainProgramName}


        AND BOARD_ID=#{boardId}
