mapper.select:
SELECT
    PART_NUMBER,
    OPERATOR_ID,
    LOT_ID,
    SUBSTR(SHOT_START_TIME,1,4)||'-'||SUBSTR(SHOT_START_TIME,5,2)||'-'||SUBSTR(SHOT_START_TIME,7,2)||' '||SUBSTR(SHOT_START_TIME,9,2)||':'||SUBSTR(SHOT_START_TIME,11,2)||':'||SUBSTR(SHOT_START_TIME,13,2) AS SHOT_START_TIME,
    SUBSTR(SHOT_END_TIME,1,4)||'-'||SUBSTR(SHOT_END_TIME,5,2)||'-'||SUBSTR(SHOT_END_TIME,7,2)||' '||SUBSTR(SHOT_END_TIME,9,2)||':'||SUBSTR(SHOT_END_TIME,11,2)||':'||SUBSTR(SHOT_END_TIME,13,2) AS SHOT_END_TIME,
    PROCESS_CODE,
    TESTER_NUMBER,
    TESTER_MODEL,
    HEAD,
    TEST_COUNTER,
    HANDLER_MODEL,
    'testTime' AS TEST_TIME,
    'indexTime' AS INDEX_TIME,
    BOARD_ID,
    DUT_MAIN_BIN
FROM CASI_BIN

    1 = 1

        AND END_TIME >= #{endTimeStart}


        AND END_TIME <= #{endTimeEnd}


        AND PART_NUMBER=#{partNumber}


        AND PROCESS_CODE=#{processCode}


        AND TESTER_MODEL=#{testerModel}


        AND TESTER_NUMBER=#{testerNumber}


        AND HEAD=#{head}


        AND TEST_COUNTER=#{testCounter}


        AND LOT_ID=#{lotId}


        AND MAIN_PROGRAM_NAME=#{mainProgramName}


        AND BOARD_ID=#{boardId}

SELECT
PART_NUMBER,
OPERATOR_ID,
LOT_ID,
LOT_IN_TIME,
END_TIME,
PROCESS_CODE,
TESTER_NUMBER,
TESTER_MODEL,
HEAD,
TEST_COUNTER,
HANDLER_MODEL,
BOARD_ID,
DUT_MAIN_BIN
FROM CASI_BIN

    1 = 1

        AND END_TIME >= #{endTimeStart}


        AND END_TIME <= #{endTimeEnd}


        AND PART_NUMBER=#{partNumber}


        AND PROCESS_CODE=#{processCode}


        AND TESTER_MODEL=#{testerModel}


        AND TESTER_NUMBER=#{testerNumber}


        AND HEAD=#{head}


        AND TEST_COUNTER=#{testCounter}


        AND LOT_ID=#{lotId}


        AND MAIN_PROGRAM_NAME=#{mainProgramName}


        AND BOARD_ID=#{boardId}
