mapper.select:
SELECT
      SEQ,
      TESTER,
      SYSTEM,
      HANDLER,
      HEAD,
      LOT_ID,
      PROCESS_CODE,
      PART_NUMBER,
      PROGRAM_NAME,
      GRADE,
      FAB,
      FW_NAME,
      FW_VERSION,
      TEMP,
      OPERATOR_ID,
      QUANTITY,
      FUNCTION_KEY,
      TEST_COUNTER,
      TEST_INPUT,
      TEST_FLOW,
      TEST_MODE,
      BOARD_ID,
      PRELOT_END_TIME,
      LOT_IN_TIME,
      BETS_IN_TIME,
      BETS_END_TIME,
      LOT_IN_END_TIME,
      LOT_START_TIME,
      END_TIME,
      BIN_IN_TIME,
      BIN_END_TIME,
      SBL_IN_TIME,
      SBL_END_TIME,
      FINAL_END_TIME,
      SBL_RESULT,
      SUB_BIN
FROM SUB_BIN

      1 = 1

        AND SEQ=#{seq}


        AND TESTER=#{tester}


        AND SYSTEM=#{system}


        AND HANDLER=#{handler}


        AND HEAD=#{head}


        AND LOT_ID=#{lotId}


        AND PROCESS_CODE=#{processCode}


        AND PART_NUMBER=#{partNumber}


        AND PROGRAM_NAME=#{programName}


        AND GRADE=#{grade}


        AND FAB=#{fab}


        AND FW_NAME=#{fwName}


        AND FW_VERSION=#{fwVersion}


        AND TEMP=#{temp}


        AND OPERATOR_ID=#{operatorId}


        AND QUANTITY=#{quantity}


        AND FUNCTION_KEY=#{functionKey}


        AND TEST_COUNTER=#{testCounter}


        AND TEST_INPUT=#{testInput}


        AND TEST_FLOW=#{testFlow}


        AND TEST_MODE=#{testMode}


        AND BOARD_ID=#{boardId}


        AND PRELOT_END_TIME=#{prelotEndTime}


        AND LOT_IN_TIME=#{lotInTime}


        AND BETS_IN_TIME=#{betsInTime}


        AND BETS_END_TIME=#{betsEndTime}


        AND LOT_IN_END_TIME=#{lotInEndTime}


        AND LOT_START_TIME=#{lotStartTime}


        AND END_TIME=#{endTime}


        AND BIN_IN_TIME=#{binInTime}


        AND BIN_END_TIME=#{binEndTime}


        AND SBL_IN_TIME=#{sblInTime}


        AND SBL_END_TIME=#{sblEndTime}


        AND FINAL_END_TIME=#{finalEndTime}


        AND SBL_RESULT=#{sblResult}


        AND SUB_BIN=#{subBin}
